$(".js-delete-comment").on("click", function() {
  var comment_id;
  if (window.confirm("削除するで？")) {
    comment_id = $(".js-delete-comment").data('comment-id');
    $.ajax({
      type: "post",
      url: location.href + "/comment/" + comment_id + "/delete",
      success: function(data) {}
    });
    return location.href = "/";
  }
});
