$(".js-delete-work").on("click", function() {
  var work_id;
  if (window.confirm("削除するで？")) {
    work_id = $(".js-delete-work").data('work-id');
    $.ajax({
      type: "post",
      url: location.href + "/work/" + work_id + "/delete",
      success: function(data) {}
    });
    return location.href = "/";
  }
});
